using System.Collections;
using System.Collections.Generic;
using UnityEngine;
using UnityEditor;

[CustomEditor(typeof(CrosshairCursor))]
public class CrosshairCursorEditor : Editor {
    public CrosshairCursor crosshair { get { return (CrosshairCursor)target; } }

    public override void OnInspectorGUI()
    {
        base.OnInspectorGUI();

        string txt = crosshair.use_text;

        txt = txt.Replace(".b", crosshair.button);
        txt = txt.Replace(".v", crosshair.verb);
        txt = txt.Replace(".t", "target");
        GUILayout.Box(txt, GUILayout.ExpandWidth(true));
        GUILayout.Label(".b = button | .v = verb | .t = target", GUILayout.ExpandWidth(true));
    }
    private static string icon = "";

    public override Texture2D RenderStaticPreview(string assetPath, Object[] subAssets, int width, int height)
    {
        if (crosshair.sprite != null)
            return PreviewUtil.RenderStaticPreview(crosshair.sprite, width, height);
        return Base64ToTexture(icon);
    }

    private static Texture2D Base64ToTexture(string base64)
    {
        Texture2D t = new Texture2D(1, 1)
        {
            hideFlags = HideFlags.HideAndDontSave
        };
        t.LoadImage(System.Convert.FromBase64String(base64));
        return t;
    }
}
